import * as React from 'react';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import CircularProgress from '@mui/material/CircularProgress';
import { Container, Typography } from '@mui/material';

export default class ImageWindow extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            image: props.image,
            uploadStatus: props.uploadStatus,
        }
        console.log(props.uploadStatus)
    }

    _CenterItem(props){
        if (props.uploadStatus == "not_uploaded"){
            return(
                <Typography>output image preview will be shown here after uploading.</Typography>
            );
        }else if (props.uploadStatus == "processing"){
            return(
                <CircularProgress color="primary" />
            );
        }else{
    
        }
    }



    render(){
        return(
            <Container maxWidth='lg' fixed>
                <Box 
                    width={'100%'}
                    height={450}
                    display="flex" 
                    alignItems="center"
                    justifyContent="center"
                    sx={{backgroundColor: 'secondary.main',
                        borderRadius: 5,
                        border: 2,
                        borderStyle: 'dashed'
                    }}>
                    <CenterItem uploadStatus = {this.state.uploadStatus}/>
                    
                </Box>
            </Container>
        )
    
