import * as React from 'react';
import Grid from '@mui/material/Grid';
import UserForm from './UserForm';
import axios from 'axios';
import ImageWindow from './ImageWindow';

const UPLOAD_URL = 'http://localhost:5000/upload';

export default class Inference extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            uploadStatus: "not_uploaded",
            image: 0,
            mask: 0
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.setState = this.setState.bind(this);
    }

    handleSubmit(imagew, imageh, uploadFiles){
        const formData = new FormData();
        for (let i = 0; i < uploadFiles.files.length; i++){
            formData.append("file", uploadFiles.files[i]);
        }
        formData.append("imagew", imagew);
        formData.append("imageh", imageh);

        this.setState((state, props) => ({
            ...this.state,
            uploadStatus: "processing"
        }));
        
        // this.setState({uploadStatus: "processing"}, ()=>{console.log(this.state.uploadStatus)})

        axios.post(UPLOAD_URL, formData).then(async (res) => {
            const url = URL.createObjectURL(res.data)
            this.setState({image: url})
            
            console.log("file upload success")
            // axios.get('http://localhost:5000/init_mask').then((res2) => {
            //     this.setState((state, props) => ({
            //         ...this.state,
            //         mask: res2.data
            //     }));
            //     console.log('mask get success!')
            // }).catch((err) => alert("mask_get_error"));
        }).catch((err) => alert(err));
        
        this.setState((state, props) => ({
            ...this.state,
            uploadStatus: "done"
        }));
    }

    nextImage(){
        axios.get("http://localhost:5000/next_image").then((res) => {
            if (res !== "error"){
                console.log("next_image");
                this.setState({image: res.data})
            }
        }).catch((err) => alert("Request Error"));
    }

    prevImage(){
        axios.get("http://localhost:5000/prev_image").then((res) => {
            if (res !== "error"){
                console.log("prev_image");
                this.setState({image: res.data})
            }
        }).catch((err) => alert("Request Error"));
    }

    nextMask(){
        axios.get("http://localhost:5000/next_mask").then((res) => {
            if (res !== "error"){
                console.log("next_mask");
                this.setState({mask: res.data})
            }
        }).catch((err) => alert("Request Error"));
    }

    prevMask(){
        axios.get("http://localhost:5000/prev_mask").then((res) => {
            if (res !== "error"){
                console.log("prev_mask");
                this.setState({mask: res.data})
            }
        }).catch((err) => alert("Request Error"));
    }


    render(){
        console.log("render: " + this.state.uploadStatus)
        return(
            <Grid container spacing={2}>
            <Grid item xs={4}>
                <UserForm 
                    submitCallback={(imagew, imageh, files) => this.handleSubmit(imagew, imageh, files)}
                    statusUpdateCallback={(param) => this.setState({uploadStatus: param})}
                />
            </Grid>
            <Grid item xs={8}>
                <ImageWindow uploadStatus={this.state.uploadStatus} thisImage={this.state.image} thisMask={this.state.mask}/>
            </Grid>
            </Grid>
            
        );
    }
    
}
