import * as React from 'react';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Files from 'react-files';

export default class UserForm extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            imagew: '',
            imageh: '',
            files: null,
        };
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleFileInput = this.handleFileInput.bind(this);
        this.submitCallback = props.submitCallback;
    }

    handleInputChange(event){
        const target = event.target;
        this.setState({
            [target.name]: target.value
        });
    }

    handleFileInput(event){
        this.setState({files: event.target.files});
    }

    handleSubmit(event){
        event.preventDefault();
        this.submitCallback(this.state.imagew, this.state.imageh, this.state.files);
    }

    render(){
        return(
            <Container component="main" maxWidth="xs">
                <form onSubmit={this.handleSubmit}>
                    <TextField 
                        margin="normal"
                        value={this.state.imagew} 
                        id="imagew" 
                        label="output image width" 
                        name="imagew" 
                        onChange = {this.handleInputChange}
                        fullWidth/>
                    <TextField 
                        margin="normal" 
                        value={this.state.imageh} 
                        name="imageh" 
                        label="output image height" 
                        id="imageh" 
                        onChange = {this.handleInputChange}
                        fullWidth/>
                    <input type="file" onChange={this.handleFileInput} multiple/>
                    {/* <Files
                        className='files-dropzone'
                        onChange={this.onFilesChange}
                        onError={this.onFilesError}
                        accepts={['image/png', '.pdf', 'audio/*']}
                        multiple
                        maxFileSize={10000000}
                        minFileSize={0}
                        clickable
                        >
                        Drop files here or click to upload
                    </Files> */}
                    <Button 
                        type="submit" 
                        variant="contained" 
                        sx={{ mt: 3, mb: 2 }}
                    > 
                    Upload and process
                    </Button>
                    {/* <Typography>
                        *.zip files are supported for multi-image uploads.
                    </Typography> */}
                </form>
            </Container>
        );
    }
    
}
