import * as React from 'react';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import CircularProgress from '@mui/material/CircularProgress';
import { Container, Typography, Button} from '@mui/material';
import testImage from './assets/1.png';

export default function ImageWindow(props){
    return(
        <Container maxWidth='lg' fixed>
            <Box 
                width={'100%'}
                height={500}
                display="flex" 
                alignItems="center"
                justifyContent="center"
                sx={{backgroundColor: 'secondary.main',
                    borderRadius: 3,
                    border: 1,
                    borderStyle: 'solid'
                }}>
                <CenterItem uploadStatus={props.uploadStatus} prevImage={props.prevImage} nextImage={props.nextImage}/>
                
            </Box>
        </Container>
    );    
}

const [imgSrc, setImgSrc] = useState('http://localhost:5000/get_image');
const [maskSrc, setMaskSrc] = useState('http://localhost:5000/get_mask')
const [fallback, setFallback] = useState(false);

useEffect( () => {
    if(img){
      setImgSrc(img);
      setMaskSrc(img)
    }
  },[img])

const reloadSrc = e => { 
    if(fallback){
      e.target.src = 'http://localhost:5000/get_image';
    }else{
      e.target.src = imgSrc
      setFallback(true)
    }
  }

function CenterItem(props){
    if (props.uploadStatus === "not_uploaded"){
        return(
            <Typography>output image preview will be shown here after uploading.</Typography>
        );
    }else if (props.uploadStatus === "processing"){
        return(
            <CircularProgress color="primary" />
        );
    }else{
        const imgSrc = 'http://localhost:5000/get_image?${new Date()}'
        const maskSrc = 'http://localhost:5000/get_mask?${new Date()}'
        return(
            <Container maxWidth='xl'>
            <Grid container spacing={2}>
                <Grid item xs={6}>
                    <img src={imgSrc} style={{margin: 10}}width={450} height={400}></img>
                    {/* <Box sx={{width: 475, height: 400}} backgroundImage={testImage} position='relative'/> */}
                </Grid>
                <Grid item xs={6}>
                    <img src={maskSrc} style={{margin: 10}} width={450} height={400} ></img>
                    {/* <Box sx={{width: 475, height: 400}} backgroundImage={testImage} position='relative'/> */}
                </Grid>
            </Grid>

            <Grid container spacing={2}>
                <Grid item xs={6}>
                    <Button variant="outlined" onClick={props.prevImage} fullWidth>Previous Image</Button>
                </Grid>
                <Grid item xs={6}>
                    <Button variant="outlined" onClick={props.nextImage} fullWidth>Next Image</Button>
                </Grid>
            </Grid>
            </Container>
            
        )
    }
}
