import * as React from 'react';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import UserForm from './UserForm';
import axios from 'axios';
import ImageWindow from './ImageWindow';

const UPLOAD_URL = 'http://localhost:5000/upload';

export default class Inference extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            
        };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(imagew, imageh, files){
        const formData = new FormData();
        formData.append("imagew", imagew);
        formData.append("imageh", imageh)
        formData.append("files", files);

        fetch(UPLOAD_URL, {
            method: 'POST',
            body: formData,
        }).then((response) => {
            response.json().then((body) => {
                this.setState({ imageURL: `http://localhost:8000/${body.file}` });
            });
        });
    }


    render(){
        console.log(typeof this.images)
        return(
            <Grid container spacing={2}>
            <Grid item xs={4}>
                <UserForm submitCallback={(imagew, imageh, files) => this.handleSubmit(imagew, imageh, files)}/>
            </Grid>
            <Grid item xs={8}>
                <ImageWindow image={0}/>
            </Grid>
            </Grid>
            
        );
    }
    
}
